import Axios from "axios";
import React, { useContext, useEffect, useReducer } from "react";
import Col from "react-bootstrap/esm/Col";
import Row from "react-bootstrap/esm/Row";
import Card from "react-bootstrap/Card";
import ListGroup from "react-bootstrap/ListGroup";
import { Helmet } from "react-helmet-async";
import { Link, useNavigate } from "react-router-dom";
import CheckoutSteps from "../component/checkoutSteps";
import { Store } from "../Store";
import Container from "react-bootstrap/esm/Container"; 
import Button from "react-bootstrap/esm/Button";
import { toast } from "react-toastify";
import { getError } from '../utils';
import LoadingBox from '../component/LoadingBox';
const reducer = (state, action) => {
    switch(action.type){
        case 'CREATE_REQUEST':
        return{...state, loading:true};
        case 'CREATE_SUCCESS':
            return{...state, loading:false};
        case 'CREATE_FAIL':
            return{...state, loading:false}
        default:
            return state;
    }
}
export default function PlaceOrderScreen() {
    const [{loading}, dispatch] = useReducer(reducer, {
        loading:false,
    }) 
    const {state, dispatch:ctxDispatch} = useContext(Store);
    const {cart, userInfo} = state;  
    const round2 = (num) => Math.round(num*100 + Number.EPSILON) / 100;
    cart.itemsPrice = round2(
        cart.cartItems.reduce((a,c) => a + c.quantity * c.price, 0)
    );
    cart.shippingPrice = cart.itemsPrice > 100 ? round2(0) : round2(10); 
    cart.taxPrice = round2(0.15 * cart.itemsPrice);
    cart.totalPrice =  cart.itemsPrice + cart.shippingPrice  + cart.taxPrice
    const navigate = useNavigate();
    const placeOrderHandler = async() => {
        try{
            dispatch({type:'CREATE_REQUEST'});
            const {data} = await Axios.post(
                'api/orders',
                {
                    orderItems:cart.cartItems,
                    shippingAddress:cart.shippingAddress,
                    paymentMethod:cart.paymentMethod,
                    itemsPrice:cart.itemsPrice,
                    shippingPrice:cart.shippingPrice,
                    taxPrice:cart.taxPrice,
                    totalPrice:cart.totalPrice
                },
                {
                    headers:{
                        authorization:`Bearer ${userInfo.token}`,
                    }
                }
            );
            ctxDispatch({type:'CART_CLEAR'});
            dispatch({type:'CREATE_SUCCESS'});
            localStorage.removeItem('cartItems');
            navigate(`/order/${data.order._id}`);

        } catch(err){
            dispatch({type:'CREATE_FAIL'});
            toast.error(getError(err));
        }
    }
    useEffect(() => {
        if(!cart.paymentMethod){
            navigate('/payment')
        }
    }, [cart, navigate]); 
    return(
        <>
        <Container>
        <CheckoutSteps step1 step2 step3 step4></CheckoutSteps>
        <Helmet>
            <title>Priview Order</title>
        </Helmet>
        <h1 className="my-3">Priview Order</h1>
        <Row>
            <Col md={8}>
                <Card className="mb-3">
                    <Card.Body>
                        <Card.Title>Shipping</Card.Title>
                        <Card.Text>
                            <strong>Name : </strong> {cart.shippingAddress.fullName}<br/>
                            <strong>Address : </strong> {cart.shippingAddress.address}<br/>
                            <strong>City : </strong> {cart.shippingAddress.city}<br/>
                            <strong>Pin Code : </strong> {cart.shippingAddress.pinCode} 
                        </Card.Text>
                        <Link to="/shipping">Edit</Link>
                    </Card.Body>
                </Card>
                <Card className="mb-3">
                    <Card.Body>
                        <Card.Title>Payment</Card.Title>
                        <Card.Text>
                            <strong>Method : </strong> {cart.paymentMethod}<br/>

                        </Card.Text>
                        <Link to="/payment">Edit</Link>
                    </Card.Body>
                </Card>
                <Card className="mb-3 ">
                    <Card.Body>
                        <Card.Title>Items</Card.Title>
                        <ListGroup variant="flush" className="buyed"> 
                            {
                                cart.cartItems.map((cartItem) => ( 
                                    <ListGroup.Item key={cartItem._id}> 
                                        <Row className="align-items-center">
                                            <Col md={6}>
                                            <img src={cartItem.image} alt={cartItem.name} 
                                            className="img-fluid rounded img-thumbnail" /> {' '}  
                                            {
                                                cartItem.selectedCol 
                                                ? <Link to={`/product/${cartItem.slug}?color=${cartItem.selectedCol}`}  >{cartItem.name}</Link>
                                                : <Link to={`/product/${cartItem.slug}`}  >{cartItem.name}</Link>
                                            }
                                            </Col>
                                            <Col md={2}>
                                                {cartItem.selectedCol ? cartItem.selectedCol : null}</Col>
                                            <Col md={2}><span>{cartItem.quantity}</span></Col>
                                            <Col md={2}> {cartItem.price} </Col>
                                        </Row>
                                    </ListGroup.Item>
                                ))
                            }
                        </ListGroup>  
                        <Link to="/cart">Edit</Link>
                    </Card.Body>
                </Card>
            </Col>
            <Col md={4}>
                <Card>
                    <Card.Body>
                        <Card.Title> Order Summary</Card.Title>
                        <ListGroup variant="flush">
                            <ListGroup.Item>
                                <Col>Items</Col>
                                <Col>{cart.itemsPrice.toFixed(2)}</Col>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Col>Shipping</Col>
                                <Col>{cart.shippingPrice.toFixed(2)}</Col>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Col>Tax</Col>
                                <Col>{cart.taxPrice.toFixed(2)}</Col>
                            </ListGroup.Item>
                            <ListGroup.Item>
                                <Col><strong>Order Total </strong></Col>
                                <Col><strong>{cart.totalPrice.toFixed(2)}</strong></Col>
                            </ListGroup.Item>
                            <ListGroup.Item>
                             <div className="d-grid">
                                <Button type="button" onClick={placeOrderHandler} disabled={cart.cartItems.length === 0}>
                                    Place Order
                                </Button>
                             </div>
                             {loading && <LoadingBox></LoadingBox>}
                            </ListGroup.Item>
                        </ListGroup>
                    </Card.Body>
                </Card>         
            </Col>
        </Row>
        </Container>
        </>
    )
}
